import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom'; // Add Link import here
import ProductService from '../service/ProductService';

function ProductAddPage() {

    const navigate = useNavigate();

    const [formData, setFormData] = useState({
        productname: '',
        productprice: '',
        productdescription: ''
    });

    const HandleProductData = (e) => {
        const { name, value } = e.target;
        setFormData({ ...formData, [name]: value });
    };


    const handleSubmit = async (e) => {

        e.preventDefault();
        try {

            console.log(formData);
            await ProductService.SaveProduct(formData);

            setFormData({
                productname: '',
                productprice: '',
                productdescription: ''
            });


         

         //   alert('Product saved successfully');
           // navigate('/');

        } catch (error) {
            console.error('Error registering product:', error);
            alert('An error occurred while saving the product');
        }
    };

    return (
        <div id='dvProductDetails'>
            <div className="ProductDetailsContainer">
                <h2>Product Details</h2>
                <form onSubmit={handleSubmit}>

                    <div className='form-group'>
                        <label>Product Name</label>
                        <input type='text' name='productName' value={formData.productname} onChange={(e) => HandleProductData(e.target.value)} />
                    </div>

                    <div className='form-group'>
                        <label>Product price</label>
                        <input type='text' name='productPrice' value={formData.productprice} onChange={(e) => HandleProductData(e.target.value)} />
                    </div>

                    <div className='form-group'>
                        <label>Product Description</label>
                        <input type='text' name='productDescription' value={formData.productdescription} onChange={(e) => HandleProductData(e.target.value)} />
                    </div>


                    <button type="submit">Save Product</button>

                    <br />
                    <Link to="/product-list" style={{ color: 'blue', fontWeight: 'bold', textDecoration: 'underline' }}>
                        View All Products!
                    </Link>
                </form>
            </div>
        </div>
    );
}

export default ProductAddPage;
