import React, { useState,useEffect} from 'react';
import UserService from '../service/UserService';
import { useNavigate,Link  } from 'react-router-dom';

function ProductAddPage() {


    const navigate = useNavigate();


    const [formData, setFormData] = useState({
        name: '',
        email: '',
        password: '',
        role: '',
        city: ''
    });

    const handleFileChange = (event) => {
        const file = event.target.files[0];
        if (file) {
            // You can store the file in formData or handle it as per your requirements
            setFormData(prevData => ({
                ...prevData,
                productImage: file // Store the selected file
            }));
        }
    };
    

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setFormData({ ...formData, [name]: value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            // Call the register method from UserService

            //const token = localStorage.getItem('token');
            //await UserService.register(formData, token);

            await UserService.register(formData);
            

            // Clear the form fields after successful registration
            setFormData({
                name: '',
                email: '',
                password: '',
                role: '',
                city: ''
            });
            alert('User registered successfully');
            navigate('/admin/user-management');

        } catch (error) {
            console.error('Error registering user:', error);
            alert('An error occurred while registering user');
        }
    };

    return (
        <div className="auth-container">
            <h2>Product Details</h2>
            <form onSubmit={handleSubmit}>
                <div className="form-group">
                    <label>Product Name</label>
                    <input type="text" name="productName" value={formData.name} onChange={handleInputChange} required />
                </div>
                <div className="form-group">
                    <label>Product Price</label>
                    <input type="number" name="productPrice" value={formData.email} onChange={handleInputChange} required />
                </div>
                <div className="form-group">
                    <label>Product Description</label>
                    <input type="text" name="productDescription" value={formData.password} onChange={handleInputChange} required />
                </div>

                <div className="form-group">
                    <label>Product Image</label>
                    <input type="file" name="productImage" accept="image/*" onChange={handleFileChange} required />
                </div>

                <button type="submit">Save Product</button>
                <br/>
                {/* <Link to="/login" style={{ color: 'blue', fontWeight: 'bold', textDecoration: 'underline' }}>Back to Login</Link> */}
            </form>
        </div>
    );
}

export default ProductAddPage;
