import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import ProductService from '../service/ProductService';
import RatingReviewService from '../service/RatingReviewService';

function ViewProduct() {
  const { id } = useParams(); // Get the product ID from the URL
  const [product, setProduct] = useState(null);
  const [review_description, setReview] = useState('');
  const [rating_value, setRating] = useState(0); // Store selected rating (1-5)

  useEffect(() => {
   // Fetch product details based on the id (dummy data in this case)

   const fetchProduct = async () => {
    // Dummy Product Data
    const dummyProducts = await ProductService.getProductById(id);

    // Set the dummy products as the initial data
    setProduct(dummyProducts.singleProduct);
    console.log(dummyProducts.singleProduct);
  };

    fetchProduct();
  }, [id]);


  const handleReviewChange = (e) => {
    setReview(e.target.value);
  };

  const handleStarClick = (index) => {
    setRating(index + 1); // Set the rating (1-5)
  };

  const handleSubmitReview = async () => {
    //alert(`Review Submitted: ${review}\nRating: ${rating} Stars`);
    

    
    try {
      const RatingReviewData = await RatingReviewService.SaveRatingReview(rating_value, review_description)
      // if(userData.token){
      //     localStorage.setItem('token', userData.token)
      //     localStorage.setItem('role', userData.role)
      //     navigate('/profile');
      //     navigate(0); 
      // }else{
      //     setError(userData.error)
      // }
      alert("Data Saved Successfully!")
  } catch (error) {
      console.log(error)
     
  }


  };


  if (!product) return <div>Loading...</div>;

  return (
    <div className="view-product-container">
      <div className="product-content">
        <div className="left-container">
          <img src={product.imageUrl} alt={product.product_name} className="product-image" />
        </div>
        <div className="right-container">
          <h2 className="product-name">{product.product_name}</h2>
          <p className="product-price">${product.product_price}</p>
          <p className="product-description">{product.product_description}</p>
         
          {/* Review Section with Star Rating */}
          <div className="star-rating">
            {[...Array(5)].map((_, index) => (
              <span
                key={index}
                className={index < rating_value ? 'filled' : 'empty'}
                onClick={() => handleStarClick(index)}
              >
                â˜…
              </span>
            ))}
          </div>

          <textarea
            className="review-textbox"
            placeholder="Write a review..."
            value={review_description}
            onChange={handleReviewChange}
          ></textarea>
          <button onClick={handleSubmitReview} className="submit-review-button">
            Submit Review
          </button>
        </div>
      </div>
    </div>
  );
}

export default ViewProduct;
