            const token = localStorage.getItem('token')
            return !!token
        }

        static isAdmin(){
            const role = localStorage.getItem('role')
            return role === "ADMIN"
        }

        static isUser(){
            const role = localStorage.getItem('role')
            return role === "USER"
        }

        static adminOnly(){
            return this.isAuthenticated() && this.isAdmin();

        }
 }

