import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom'; // Add Link import here
import UserService from '../service/UserService';
import ProductService from '../service/ProductService';

function ProductAddPage() {
    const navigate = useNavigate();

    const [formData, setFormData] = useState({
        productName: '',
        productPrice: '',
        productDescription: '',
        productImage: null
    });

    const handleFileChange = (event) => {
        const file = event.target.files[0];
        if (file) {
            // Store the selected file in formData
            setFormData(prevData => ({
                ...prevData,
                productImage: file
            }));
        }
    };

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setFormData({ ...formData, [name]: value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            // Assuming that the 'productImage' field will be handled by the backend
            const formDataToSubmit = new FormData();
            formDataToSubmit.append('productName', formData.productName);
            formDataToSubmit.append('productPrice', formData.productPrice);
            formDataToSubmit.append('productDescription', formData.productDescription);
            if (formData.productImage) {
                formDataToSubmit.append('productImage', formData.productImage);
            }
            console.log
            // Call the API method to register the product
            await ProductService.SaveProduct(formDataToSubmit);

            // Clear form fields after successful registration
            setFormData({
                productName: '',
                productPrice: '',
                productDescription: '',
                productImage: null
            });

            alert('Product saved successfully');
            navigate('/admin/product-management');

        } catch (error) {
            console.error('Error registering product:', error);
            alert('An error occurred while saving the product');
        }
    };

    return (
        <div id='dvProductDetails'>
            <div className="ProductDetailsContainer">
                <h2>Product Details</h2>
                <form onSubmit={handleSubmit}>
                    <div className="form-group">
                        <label>Product Name</label>
                        <input
                            type="text"
                            name="productName"
                            value={formData.productName}
                            onChange={handleInputChange}
                            required
                            style={{ width: '100%', paddingTop: '8px', paddingBottom: '8px', boxSizing: 'border-box', border: '1px solid #ccc' }}
                        />
                    </div>
                    <div className="form-group">
                        <label>Product Price</label>
                        <input
                            type="number"
                            name="productPrice"
                            value={formData.productPrice}
                            onChange={handleInputChange}
                            required
                            style={{ width: '100%', padding: '8px', boxSizing: 'border-box', border: '1px solid #ccc' }}
                        />
                    </div>
                    <div className="form-group">
                        <label>Product Description</label>
                        <input
                            type="text"
                            name="productDescription"
                            value={formData.productDescription}
                            onChange={handleInputChange}
                            required
                            style={{ width: '100%', paddingTop: '8px', paddingBottom: '8px', boxSizing: 'border-box', border: '1px solid #ccc' }}
                        />
                    </div>

                    <div className="form-group">
                        <label>Product Image</label>
                        <input
                            type="file"
                            name="productImage"
                            accept="image/*"
                            onChange={handleFileChange}
                        />
                    </div>

                    <button type="submit">Save Product</button>

                    <br />
                    <Link to="/product-list" style={{ color: 'blue', fontWeight: 'bold', textDecoration: 'underline' }}>
                        View All Products!
                    </Link>
                </form>
            </div>
        </div>
    );
}

export default ProductAddPage;
