import axios from "axios";

 class RatingReviewService{

        static BASE_URL = "http://localhost:1010";

       
        static async SaveRatingReview(rating, review){
            try{
                const response = await axios.post(`${RatingReviewService.BASE_URL}/auth/ReviewRating-add`,{email, password} ,review)
                return response.data;

            }catch(error){
                throw error;
            }   
        }

    

        /*AUTHENTICATION CHECKER*/

        static logout(){
            localStorage.removeItem('token')
            localStorage.removeItem('role')

        }

        static isAuthenticated(){
            const token = localStorage.getItem('token')
            return !!token
        }

        static isAdmin(){
            const role = localStorage.getItem('role')
            return role === "ADMIN"
        }

        static isUser(){
            const role = localStorage.getItem('role')
            return role === "USER"
        }

        static adminOnly(){
            return this.isAuthenticated() && this.isAdmin();

        }
 }

 export default RatingReviewService;
