import React, { useState,useEffect } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import RatingReviewService from '../service/RatingReviewService';

function ViewProduct() {
  const { id } = useParams();
  const navigate = useNavigate();

  useEffect(() => {
    fetchProductDataById(id); // Pass the userId to fetchUserDataById
  }, [id]);

  const fetchProductDataById = async (prodId) => {
    try {
    
      const response = await ProductService.getProductById(prodId); // Pass userId to getUserById
      console.log(response);
      const { product_name, product_price, product_description } = response.singleProduct;

      setProdData({ product_name, product_price, product_description })
     
    } catch (error) {
      console.error('Error fetching user data:', error);
    }
  };



  useEffect(() => {
    // Fetch product details based on the id (dummy data in this case)
    const fetchProduct = () => {
      const dummyProducts = [
        { id: 1, name: "Wireless Headphones", description: "High-quality wireless headphones with noise cancellation.", rating: 4, price: 100, imageUrl: "https://www.gonoise.com/cdn/shop/files/CompressJPEG.online_500x500-image.png?v=1687934644" },
        { id: 2, name: "Smart Watch", description: "Track your health, notifications, and more with this stylish smart watch.", rating: 5, price: 150, imageUrl: "https://www.gonoise.com/cdn/shop/files/1_ca923369-0c7a-45d7-8edc-7a487a3b5fc6.png?v=1711280604" },
        { id: 3, name: "Gaming Laptop", description: "A powerful gaming laptop with a fast processor and high-end GPU.", rating: 3, price: 1200, imageUrl: "https://m.media-amazon.com/images/I/71XOnGsI3gL._SX679_.jpg" },
        { id: 4, name: "Bluetooth Speaker", description: "Portable Bluetooth speaker with great sound quality and long battery life.", rating: 4, price: 50, imageUrl: "https://m.media-amazon.com/images/I/51CV7YPyrGL._SX300_SY300_QL70_FMwebp_.jpg" },
        { id: 5, name: "Smartphone", description: "Latest model smartphone with a stunning camera and long-lasting battery.", rating: 5, price: 600, imageUrl: "https://m.media-amazon.com/images/I/41fCDR6pjpL._SX300_SY300_QL70_FMwebp_.jpg" },
      ];

      const product = dummyProducts.find(p => p.id === parseInt(id));
      setProduct(product);
    };

    fetchProduct();
  }, [id]);















  const [formData, setFormData] = useState({
    rating_value: 0,
    review_description: '',
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleStarClick = (index) => {
    setFormData({ ...formData, rating_value: index + 1 }); // Set the rating (1-5)
  };

  const handleSubmitReview = async (e) => {
    e.preventDefault();
    try {
      console.log("Rating Value:", formData.rating_value);
      console.log("Review Description:", formData.review_description);
      await RatingReviewService.SaveRatingReview(formData);

      // Clear the form fields after successful submission
      setFormData({
        rating_value: 0,
        review_description: '',
      });
      alert('Review submitted successfully');
    } catch (error) {
      console.error('Error submitting review:', error);
      alert('An error occurred while submitting the review');
    }
  };

  return (
    <div className="view-product-container">
      <div className="product-content">
        <div className="right-container">
          <h2 className="product-name">Submit Your Review</h2>

          {/* Review Section with Star Rating */}
          <div className="star-rating">
            {[...Array(5)].map((_, index) => (
              <span
                key={index}
                className={index < formData.rating_value ? 'filled' : 'empty'}
                onClick={() => handleStarClick(index)}
              >
                â˜…
              </span>
            ))}
          </div>

          <textarea
            className="review-textbox"
            name="review_description"
            placeholder="Write your review here..."
            value={formData.review_description}
            onChange={handleInputChange}
            required
          ></textarea>

          <button onClick={handleSubmitReview} className="submit-review-button">
            Submit Review
          </button>

          <br />
          <Link to="/login" style={{ color: 'blue', fontWeight: 'bold', textDecoration: 'underline' }}>
            Back to Login
          </Link>
        </div>
      </div>
    </div>
  );
}

export default ViewProduct;
