import React, { useState, useEffect } from 'react';
import UserService from '../service/UserService';
import { Link } from 'react-router-dom';

function ProfilePage() {
    const [profileInfo, setProfileInfo] = useState({});

    useEffect(() => {
        fetchProfileInfo();
    }, []);

    const fetchProfileInfo = async () => {
        try {
            const token = localStorage.getItem('token'); // Retrieve the token from localStorage
            const response = await UserService.getYourProfile(token);
            setProfileInfo(response.ourUsers);
        } catch (error) {
            console.error('Error fetching profile information:', error);
        }
    };

    return (
        <div className="profile-page-container">
            <div className="profile-card">
                <h2 className="profile-title">Your Profile</h2>
                <div className="profile-details">
                    <div className="profile-item">
                        <strong>Name:</strong> <span>{profileInfo.name}</span>
                    </div>
                    <div className="profile-item">
                        <strong>Email:</strong> <span>{profileInfo.email}</span>
                    </div>
                    <div className="profile-item">
                        <strong>City:</strong> <span>{profileInfo.city}</span>
                    </div>
                </div>
                
                {profileInfo.role === "ADMIN" && (
                    <div className="admin-action">
                        <button className="update-button">
                            <Link to={`/update-user/${profileInfo.id}`} className="update-link">Update Profile</Link>
                        </button>
                    </div>
                )}
            </div>

            <style jsx>{`
                .profile-page-container {
                    display: flex;
                    justify-content: center;
                    align-items: center;
                    min-height: 100vh;
                    background-color: #f4f7fc;
                }

                .profile-card {
                    background-color: #fff;
                    border-radius: 10px;
                    box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);
                    padding: 20px;
                    max-width: 400px;
                    width: 100%;
                }

                .profile-title {
                    font-size: 1.8rem;
                    font-weight: bold;
                    color: #333;
                    text-align: center;
                    margin-bottom: 20px;
                }

                .profile-details {
                    margin-bottom: 20px;
                }

                .profile-item {
                    margin-bottom: 10px;
                    font-size: 1.1rem;
                }

                .profile-item strong {
                    color: #555;
                }

                .profile-item span {
                    font-weight: normal;
                    color: #444;
                }

                .admin-action {
                    display: flex;
                    justify-content: center;
                }

                .update-button {
                    background-color: #4CAF50;
                    color: white;
                    padding: 10px 20px;
                    font-size: 1.1rem;
                    border: none;
                    border-radius: 5px;
                    cursor: pointer;
                    transition: background-color 0.3s;
                }

                .update-button:hover {
                    background-color: #45a049;
                }

                .update-link {
                    text-decoration: none;
                    color: white;
                }
            `}</style>
        </div>
    );
}

export default ProfilePage;
