import React, { useState, useEffect } from 'react';
import { useParams, useNavigate,Link } from 'react-router-dom';
import UserService from '../service/UserService';

function UpdateProduct() {
    
  const navigate = useNavigate();
  const { prodId } = useParams();


  const [prodData, setProdData] = useState({
        product_name: '',
        product_price: '',
        product_description: ''
  });

  useEffect(() => {
    fetchProductDataById(prodId); // Pass the userId to fetchUserDataById
  }, [prodId]); //wheen ever there is a chane in userId, run this

  const fetchProductDataById = async (prodId) => {
    try {
      const token = localStorage.getItem('token');
      const response = await UserService.getUserById(prodId, token); // Pass userId to getUserById
      const { name, email, role, city } = response.ourUsers;
      setUserData({ name, email, role, city });
    } catch (error) {
      console.error('Error fetching user data:', error);
    }
  };


  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setUserData((prevUserData) => ({
      ...prevUserData,
      [name]: value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const confirmDelete = window.confirm('Are you sure you want to delete this user?');
      if (confirmDelete) {
        const token = localStorage.getItem('token');
        const res = await UserService.updateUser(prodId, userData, token);
        console.log(res)
        // Redirect to profile page or display a success message
        navigate("/admin/user-management")
      }

    } catch (error) {
      console.error('Error updating user profile:', error);
      alert(error)
    }
  };

  return (
    <div className="auth-container">
      <h2>Update User</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label>Name:</label>
          <input type="text" name="name" value={userData.name} onChange={handleInputChange} />
        </div>
        <div className="form-group">
          <label>Email:</label>
          <input type="email" name="email" value={userData.email} onChange={handleInputChange} />
        </div>
        <div className="form-group">
          <label>Role:</label>
          <input type="text" name="role" value={userData.role} onChange={handleInputChange} />
        </div>
        <div className="form-group">
          <label>City:</label>
          <input type="text" name="city" value={userData.city} onChange={handleInputChange} />
        </div>
        <button type="submit">Update</button>
        <br/>
        <Link to="/profile" style={{ color: 'blue', fontWeight: 'bold', textDecoration: 'underline' }}>Back to profile</Link>
      </form>
    </div>
  );
}

export default UpdateProduct;
