import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom'; // Add Link import here
import ProductService from '../service/ProductService';

function ProductAddPage() {

    const navigate = useNavigate();

    const [formData, setFormData] = useState({
        product_name: '',
        product_price: '',
        product_description: '',
        product_image: null // Added product_image state for file
    });

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setFormData({ ...formData, [name]: value });
    };

    // Handle image file change
    const handleImageChange = (e) => {
        const file = e.target.files[0];  // Get the selected file
        setFormData({ ...formData, product_image: file });  // Update product_image with the file
    };


    const handleSubmit = async (e) => {

        e.preventDefault();
        // try {

        //     console.log(formData);
        //     await ProductService.SaveProduct(formData);

        //     setFormData({
        //         product_name: '',
        //         product_price: '',
        //         product_description: ''
        //     });

        //     alert('Product saved successfully');
        //     // navigate('/');

        // } catch (error) {
        //     console.error('Error registering product:', error);
        //     alert('An error occurred while saving the product');
        // }


        try {
            const formDataToSubmit = new FormData();
            formDataToSubmit.append('product', JSON.stringify({
                product_name: formData.product_name,
                product_price: formData.product_price,
                product_description: formData.product_description
            }));

            if (formData.product_image) {
                formDataToSubmit.append('image', formData.product_image); // Append image file to FormData
            }

            console.log(formData);
            await ProductService.SaveProduct(formDataToSubmit);

            // setFormData({
            //     product_name: '',
            //     product_price: '',
            //     product_description: '',
            //     product_image: null
            // });

            alert('Product saved successfully');
            // navigate('/');

        } catch (error) {
            console.error('Error registering product:', error);
            alert('An error occurred while saving the product');
        }






    };

    return (
        <div id='dvProductDetails'>
            <div className="ProductDetailsContainer">
                <h2>Product Details</h2>
                <form onSubmit={handleSubmit}>

                    <div className='form-group'>
                        <label>Product Name</label>
                        <input type='text' name='product_name' value={formData.product_name} onChange={handleInputChange} />
                    </div>

                    <div className='form-group'>
                        <label>Product price</label>
                        <input type='text' name='product_price' value={formData.product_price} onChange={handleInputChange} />
                    </div>

                    <div className='form-group'>
                        <label>Product Description</label>
                        <input type='text' name='product_description' value={formData.product_description} onChange={handleInputChange} />
                    </div>
                    {/* Image Upload Field */}
                    <div className='form-group'>
                        <label>Product Image</label>
                        <input
                            type="file"
                            name="product_image"
                            accept="image/*"
                            onChange={handleImageChange}  // Handle the image change
                        />
                    </div>

                    <button type="submit">Save Product</button>

                    <br />
                    <Link to="/product-list" style={{ color: 'blue', fontWeight: 'bold', textDecoration: 'underline' }}>
                        View All Products!
                    </Link>
                </form>
            </div>
        </div>
    );
}

export default ProductAddPage;
