import React, { useState, useEffect } from 'react';
import { useNavigate, Link, useParams } from 'react-router-dom';
import RatingReviewService from '../service/RatingReviewService';
import ProductService from '../service/ProductService';

function ViewProduct() {
  const { prodId } = useParams();
  const navigate = useNavigate();

  const userId = localStorage.getItem('userId');

  const [prodData, setProdData] = useState({
    product_name: '',
    product_price: '',
    product_description: '',
    product_image: null
  });


  useEffect(() => {
    fetchProductDataById(prodId); // Pass the userId to fetchUserDataById
    checkIfReviewExists(prodId, userId); 
  }, [prodId]);

  const fetchProductDataById = async (prodId) => {
    try {

      const response = await ProductService.getProductById(prodId); // Pass userId to getUserById
     
      const { product_name, product_price, product_description,product_image } = response.singleProduct;

      setProdData({ product_name, product_price, product_description,product_image })

    } catch (error) {
      console.error('Error fetching product data:', error);
    }
  };

 // Check if review already exists for the product and user
 const checkIfReviewExists = async (prodId, userId) => {
  try {
    const reviewResponse = await RatingReviewService.getReviewByProductAndUser(prodId, userId);
    if (reviewResponse && reviewResponse.review) {
      // If review exists, update formData and log to console
      const { rating_value, review_description } = reviewResponse.review;
      setFormData({ rating_value, review_description, product_id: prodId, user_id: userId });
      console.log('Existing review:', reviewResponse.review);
    }
  } catch (error) {
    console.error('Error checking review:', error);
  }
};





  const [formData, setFormData] = useState({
 
    rating_value: 0,
    review_description: '',
    product_id: prodId, // Add prodId to the form data,
    user_id: userId
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleStarClick = (index) => {
    setFormData({ ...formData, rating_value: index + 1 }); // Set the rating (1-5)
  };

  const handleSubmitReview = async (e) => {
    e.preventDefault();
    try {
    
      await RatingReviewService.SaveRatingReview(formData);

      // Clear the form fields after successful submission
      setFormData({
        rating_value: 0,
        review_description: ''
      });
      alert('Review submitted successfully');
      navigate(0); 
    } catch (error) {
      console.error('Error submitting review:', error);
      alert('An error occurred while submitting the review');
    }
  };

  return (
    <div className="view-product-container">
      <div className="product-content">
        <div className="left-container">

          <img 
              src={`data:image/jpeg;base64,${prodData.product_image}`} 
              alt={prodData.product_name} 
              className="product-image" 
            />


        </div>

        <div className="right-container">
        <h2 className="product-name">{prodData.product_name}</h2>
        <p className="product-price">${prodData.product_price}</p>
        <p className="product-description">{prodData.product_description}</p>


          {/* Review Section with Star Rating */}
          <div className="star-rating">
            {[...Array(5)].map((_, index) => (
              <span
                key={index}
                className={index < formData.rating_value ? 'filled' : 'empty'}
                onClick={() => handleStarClick(index)}
              >
                â˜…
              </span>
            ))}
          </div>

          <textarea
            className="review-textbox"
            name="review_description"
            placeholder="Write your review here..."
            value={formData.review_description}
            onChange={handleInputChange}
            required
          ></textarea>

          <button onClick={handleSubmitReview} className="submit-review-button">
            Submit Review
          </button>

          <br />
          <Link to="/product-list" style={{ color: 'blue', fontWeight: 'bold', textDecoration: 'underline' }}>
            Back to Product List
          </Link>
        </div>
      </div>

    </div>
  );
}

export default ViewProduct;
