import React, { useState, useEffect } from 'react';
import { useParams, useNavigate, Link } from 'react-router-dom';
import ProductService from '../service/ProductService';
import '../../style/productadd.css'; // Ensure the same styling is applied

function UpdateProduct() {
  const navigate = useNavigate();
  const { prodId } = useParams();

  const [prodData, setProdData] = useState({
    product_name: '',
    product_price: '',
    product_description: ''
  });

  useEffect(() => {
    fetchProductDataById(prodId); // Fetch product data by id when the page loads
  }, [prodId]);

  const fetchProductDataById = async (prodId) => {
    try {
      const response = await ProductService.getProductById(prodId); 
      const { product_name, product_price, product_description } = response.singleProduct;
      setProdData({ product_name, product_price, product_description });
    } catch (error) {
      console.error('Error fetching product data:', error);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setProdData((prevProdData) => ({
      ...prevProdData,
      [name]: value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const confirmUpdate = window.confirm('Are you sure you want to update this product?');
      if (confirmUpdate) {
        await ProductService.updateProduct(prodId, prodData);
        alert('Product Updated Successfully!');
        navigate('/product-list'); // Redirect to product list page after updating
      }
    } catch (error) {
      console.error('Error updating product:', error);
      alert('Error updating product');
    }
  };

  return (
    <div className="product-add-container">
      <div className="product-form-container">
        <h2 className="form-title">Update Product</h2>
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label htmlFor="product_name">Product Name</label>
            <input
              type="text"
              id="product_name"
              name="product_name"
              value={prodData.product_name}
              onChange={handleInputChange}
              placeholder="Enter product name"
              required
            />
          </div>

          <div className="form-group">
            <label htmlFor="product_price">Product Price</label>
            <input
              type="text"
              id="product_price"
              name="product_price"
              value={prodData.product_price}
              onChange={handleInputChange}
              placeholder="Enter product price"
              required
            />
          </div>

          <div className="form-group">
            <label htmlFor="product_description">Product Description</label>
            <textarea
              id="product_description"
              name="product_description"
              value={prodData.product_description}
              onChange={handleInputChange}
              placeholder="Enter product description"
              rows="4"
              required
            />
          </div>

          <button type="submit" className="submit-btn">Update Product</button>
        </form>

        <Link to="/product-list" className="view-products-link">View All Products</Link>
      </div>
    </div>
  );
}

export default UpdateProduct;
