import React, { useState, useEffect } from 'react';
import UserService from '../service/UserService';
import { useNavigate, Link } from 'react-router-dom';
import RatingReviewService from '../service/RatingReviewService';

function ViewProduct() {


    const navigate = useNavigate();


    const [formData, setFormData] = useState({
      rating_value: '',
      review_description: '',
    
    });



    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setFormData({ ...formData, [name]: value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            // Call the register method from UserService

            //const token = localStorage.getItem('token');
            //await UserService.register(formData, token);

            await RatingReviewService.SaveRatingReview(formData);


            // Clear the form fields after successful registration
            setFormData({
              rating_value: '',
              review_description: '',
            });
            alert('User registered successfully');
            navigate('/admin/user-management');

        } catch (error) {
            console.error('Error registering user:', error);
            alert('An error occurred while registering user');
        }
    };

    return (


        <div id='RegisterContainer'>
            <div className="auth-container registerPage" >
                <h2>Registration</h2>
                <form onSubmit={handleSubmit}>
                    <div className="form-group">
                        <label>Rating</label>
                        <input type="text" name="rating_value" value={formData.rating_value} onChange={handleInputChange} required />
                    </div>
                    <div className="form-group">
                        <label>Review:</label>
                        <input type="text" name="review_description" value={formData.review_description} onChange={handleInputChange} required />
                    </div>
                
                    <button type="submit">Register</button>
                    <br />
                    <Link to="/login" style={{ color: 'blue', fontWeight: 'bold', textDecoration: 'underline' }}>Back to Login</Link>
                </form>
            </div>
        </div>

    );
}

export default ViewProduct;
