import axios from "axios";

class RatingReviewService {

    static BASE_URL = "http://localhost:1010";



    static async SaveRatingReview(userData) {
        try {
            const response = await axios.post(`${RatingReviewService.BASE_URL}/auth/ReviewRating-add`, userData)
            return response.data;

        } catch (error) {
            throw error;
        }
    }


    static async getExistRatingReviewByUserId(prodId,userId) {
        try {

            const response = await axios.get(`${RatingReviewService.BASE_URL}/auth/ReviewRating-existing/`,{prodId, userId})
            return response.data;
            confirm
        } catch (error) {
            throw error;
        }
    }


    /*AUTHENTICATION CHECKER*/

    static logout() {
        localStorage.removeItem('token')
        localStorage.removeItem('role')

    }

    static isAuthenticated() {
        const token = localStorage.getItem('token')
        return !!token
    }

    static isAdmin() {
        const role = localStorage.getItem('role')
        return role === "ADMIN"
    }

    static isUser() {
        const role = localStorage.getItem('role')
        return role === "USER"
    }

    static adminOnly() {
        return this.isAuthenticated() && this.isAdmin();

    }
}

export default RatingReviewService;
