import React, { useState, useEffect } from 'react';
import { useNavigate, Link, useParams } from 'react-router-dom';
import RatingReviewService from '../service/RatingReviewService';
import ProductService from '../service/ProductService';

function ViewProduct() {
  const { prodId } = useParams();
  const navigate = useNavigate();
  const userId = localStorage.getItem('userId');

  const [prodData, setProdData] = useState({
    product_name: '',
    product_price: '',
    product_description: '',
    product_image: null
  });

  const [reviews, setReviews] = useState([]);
  const [isEditing, setIsEditing] = useState(false);
  const [editingReviewId, setEditingReviewId] = useState(null);

  const [formData, setFormData] = useState({
    rating_value: 0,
    review_description: '',
    product_id: prodId,
    user_id: userId
  });

  useEffect(() => {
    fetchProductDataById(prodId);
    checkIfReviewExists(prodId, userId);
  }, [prodId]);

  const fetchProductDataById = async (prodId) => {
    try {
      const response = await ProductService.getProductById(prodId);
      const { product_name, product_price, product_description, product_image } = response.singleProduct;
      setProdData({ product_name, product_price, product_description, product_image });
    } catch (error) {
      console.error('Error fetching product data:', error);
    }
  };

  const checkIfReviewExists = async (prodId, userId) => {
    try {
      const requestData = { product_id: prodId, user_id: userId };
      const reviewResponse = await RatingReviewService.getExistRatingReviewByUserId(requestData);
      setReviews(reviewResponse.ratingReviewDtoList || []);
    } catch (error) {
      console.error('Error checking review:', error);
      setReviews([]);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleStarClick = (index) => {
    setFormData({ ...formData, rating_value: index + 1 });
  };

  const handleSubmitReview = async (e) => {
    e.preventDefault();
    try {
      if (isEditing) {
        await RatingReviewService.updateRatingReview(editingReviewId, formData);
        alert('Review updated successfully');
        setIsEditing(false);
        setEditingReviewId(null);
      } else {
        await RatingReviewService.SaveRatingReview(formData);
        alert('Review submitted successfully');
      }
      setFormData({ rating_value: 0, review_description: '' });
      checkIfReviewExists(prodId, userId);
    } catch (error) {
      console.error('Error submitting review:', error);
      alert('An error occurred while submitting the review');
    }
  };

  const handleEditClick = (review) => {
    setIsEditing(true);
    setEditingReviewId(review.rating_review_id);
    setFormData({
      rating_value: review.rating_value,
      review_description: review.review_description,
      product_id: review.product_id,
      user_id: review.user_id
    });
  };

  return (
    <div className="view-product-container">
      <style>
        {`
          .view-product-container {
            font-family: Arial, sans-serif;
            padding: 20px;
            max-width: 900px;
            margin: auto;
          }

          .product-content {
            display: flex;
            gap: 20px;
            border: 1px solid #ccc;
            padding: 20px;
            border-radius: 10px;
            background-color: #f9f9f9;
          }

          .left-container {
            flex: 1;
          }

          .product-image {
            width: 100%;
            border-radius: 10px;
          }

          .right-container {
            flex: 2;
            display: flex;
            flex-direction: column;
          }

          .product-name {
            font-size: 1.8rem;
            font-weight: bold;
            margin-bottom: 10px;
          }

          .product-price {
            font-size: 1.5rem;
            color: #28a745;
            margin-bottom: 10px;
          }

          .product-description {
            font-size: 1rem;
            color: #666;
            margin-bottom: 20px;
          }

          .add-review {
            margin-top: 20px;
          }

          .add-review h3 {
            font-size: 1.2rem;
            margin-bottom: 10px;
          }

          .star-rating span {
            font-size: 1.5rem;
            cursor: pointer;
            color: #ffc107;
          }

          .star-rating span.empty {
            color: #ddd;
          }

          .review-textbox {
            width: 100%;
            padding: 10px;
            margin: 10px 0;
            border: 1px solid #ccc;
            border-radius: 5px;
            resize: vertical;
          }

          .submit-review-button {
            background-color: #007bff;
            color: white;
            border: none;
            padding: 10px 20px;
            border-radius: 5px;
            cursor: pointer;
            font-size: 1rem;
          }

          .submit-review-button:hover {
            background-color: #0056b3;
          }

          .existing-reviews {
            margin-top: 30px;
          }

          .reviews-container {
            display: flex;
            flex-direction: column;
            gap: 15px;
          }

          .review-card {
            padding: 15px;
            border: 1px solid #ccc;
            border-radius: 5px;
            background-color: #fff;
          }

          .review-header {
            display: flex;
            justify-content: space-between;
            align-items: center;
            margin-bottom: 10px;
          }

          .edit-button {
            background-color: #ffc107;
            color: black;
            border: none;
            padding: 5px 10px;
            border-radius: 5px;
            cursor: pointer;
          }

          .edit-button:hover {
            background-color: #e0a800;
          }

          .back-link {
            display: inline-block;
            margin-top: 20px;
            color: #007bff;
            text-decoration: none;
            font-size: 1rem;
          }

          .back-link:hover {
            text-decoration: underline;
          }
            

          .review-rating span {
  font-size: 1.2rem;
  color: #ddd; /* Default gray for empty stars */
}

.review-rating span.filled {
  color: #ffc107; /* Yellow for filled stars */
}

        `}
      </style>

      <div className="product-content">
        <div className="left-container">
          <img
            src={`data:image/jpeg;base64,${prodData.product_image}`}
            alt={prodData.product_name}
            className="product-image"
          />
        </div>
        <div className="right-container">
          <h2 className="product-name">{prodData.product_name}</h2>
          <p className="product-price">${prodData.product_price}</p>
          <p className="product-description">{prodData.product_description}</p>

          <div className="add-review">
            <h3>{isEditing ? 'Edit Your Review' : 'Write Your Review'}</h3>
            <div className="star-rating">
              {[...Array(5)].map((_, index) => (
                <span
                  key={index}
                  className={index < formData.rating_value ? 'filled' : 'empty'}
                  onClick={() => handleStarClick(index)}
                >
                  ★
                </span>
              ))}
            </div>
            <textarea
              className="review-textbox"
              name="review_description"
              placeholder="Write your review here..."
              value={formData.review_description}
              onChange={handleInputChange}
              required
            ></textarea>
            <button onClick={handleSubmitReview} className="submit-review-button">
              {isEditing ? 'Update Review' : 'Submit Review'}
            </button>
            <br />
            <Link to="/product-list" className="back-link">
              Back to Product List
            </Link>
          </div>
        </div>
      </div>

      {reviews.length > 0 && (
        <div className="existing-reviews">
          <h3>Customer Reviews</h3>
          <div className="reviews-container">
            {reviews.map((review) => (
              <div key={review.rating_review_id} className="review-card">
                <div className="review-header">
                  <div className="review-rating">
                    {[...Array(review.rating_value)].map((_, index) => (
                      <span key={index} className="filled">★</span>
                    ))}
                  </div>
                  {review.user_id == userId && (
                    <button
                      className="edit-button"
                      onClick={() => handleEditClick(review)}
                    >
                      Edit
                    </button>
                  )}
                </div>
                <p className="review-description">{review.review_description}</p>
              </div>
            ))}
          </div>
        </div>
      )}
    </div>
  );
}

export default ViewProduct;
