import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import ProductService from '../service/ProductService';
import RatingReviewService from '../service/RatingReviewService';

function ViewProduct() {
  const { id } = useParams(); // Get the product ID from the URL
  const [product, setProduct] = useState(null);
  
  const [formData, setFormData] = useState({
    rating_value: 0,
    review_description: ''
  });

  useEffect(() => {
    // Fetch product details based on the id
    const fetchProduct = async () => {
      try {
        const dummyProducts = await ProductService.getProductById(id);
        setProduct(dummyProducts.singleProduct);
      } catch (error) {
        console.log("Error fetching product:", error);
      }
    };

    fetchProduct();
  }, [id]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleStarClick = (index) => {
    setFormData({ ...formData, rating_value: index + 1 }); // Set the rating (1-5)
  };

  const handleSubmitReview = async () => {
    console.log("Rating Value:", formData.rating_value);
    console.log("Review Description:", formData.review_description);

    try {
      const RatingReviewData = await RatingReviewService.SaveRatingReview(formData.rating_value, formData.review_description);
      // Handle success (e.g., show a success message)
      console.log('Review submitted successfully', RatingReviewData);
    } catch (error) {
      console.log("Error saving review:", error);
    }
  };

  // If the product is not yet loaded, show a loading message
  if (!product) {
    return <div>Loading...</div>;
  }

  return (
    <div className="view-product-container">
      <div className="product-content">
        <div className="right-container">
          <h2 className="product-name">{product.product_name}</h2>
          <p className="product-price">${product.product_price}</p>
          <p className="product-description">{product.product_description}</p>

          {/* Review Section with Star Rating */}
          <div className="star-rating">
            {[...Array(5)].map((_, index) => (
              <span
                key={index}
                className={index < formData.rating_value ? 'filled' : 'empty'}
                onClick={() => handleStarClick(index)}
              >
                â˜…
              </span>
            ))}
          </div>

          <textarea
            className="review-textbox"
            name="review_description"
            placeholder="Write a review..."
            value={formData.review_description}
            onChange={handleInputChange}
          ></textarea>
          
          <button onClick={handleSubmitReview} className="submit-review-button">
            Submit Review
          </button>
        </div>
      </div>
    </div>
  );
}

export default ViewProduct;
