import React, { useState } from 'react';
import { NavLink, useNavigate } from 'react-router-dom';
import UserService from '../service/UserService';

function Navbar() {
    const [activeLink, setActiveLink] = useState('');
    const isAuthenticated = UserService.isAuthenticated();
    const isAdmin = UserService.isAdmin();
    const navigate = useNavigate();

    const handleLogout = () => {
        const confirmDelete = window.confirm('Are you sure you want to logout this user?');
        if (confirmDelete) {
            UserService.logout();
            navigate('/');
        }
    };

    // Function to handle setting the active link based on the text
    const handleLinkClick = (text) => {
        setActiveLink(text);  // Set the active link to the clicked navbar text
    };

    return (
        <>
            <style>
                {`
                    nav {
                        padding: 10px;
                        background-color: #333;
                    }
                    ul {
                        list-style: none;
                        padding: 0;
                    }
                    li {
                        display: inline-block;
                        margin: 0 15px;
                    }
                    a {
                        text-decoration: none;
                        color: #fff;
                        padding: 5px 10px;
                        transition: all 0.3s ease;
                    }
                    a:hover {
                        color: #f39c12;
                        font-weight: bold;
                    }
                    a:focus {
                        color: #f39c12;
                        font-weight: bold;
                    }
                    .active {
                        color: #f39c12;
                        font-weight: bold;
                    }
                `}
            </style>
            <nav>
                <ul>
                    {/* E-Commerce Website link (No active class applied) */}
                    {!isAuthenticated && (
                        <li>
                            <NavLink 
                                to="/" 
                                onClick={() => handleLinkClick('E-Commerce Website')}
                            >
                                E-Commerce Website
                            </NavLink>
                        </li>
                    )}

                    {/* Profile link */}
                    {isAuthenticated && (
                        <li>
                            <NavLink 
                                to="/profile"
                                className={activeLink === 'Profile' ? 'active' : ''}
                                onClick={() => handleLinkClick('Profile')}
                            >
                                Profile
                            </NavLink>
                        </li>
                    )}

                    
                    {/* Admin User Management */}
                    {isAdmin && (
                        <li>
                            <NavLink 
                                to="/admin/user-management"
                                className={activeLink === 'User Management' ? 'active' : ''}
                                onClick={() => handleLinkClick('User Management')}
                            >
                                User Management
                            </NavLink>
                        </li>
                    )}

                    {/* Admin Product Section */}
                    {isAdmin && isAuthenticated && (
                        <li>
                            <NavLink 
                                to="/auth/product-add"
                                className={activeLink === 'Product Section' ? 'active' : ''}
                                onClick={() => handleLinkClick('Product Section')}
                            >
                                Product Management
                            </NavLink>
                        </li>
                    )}

                    {/* Product List */}
                    {isAuthenticated && (
                        <li>
                            <NavLink 
                                to="/auth/all-products"
                                className={activeLink === 'Product List' ? 'active' : ''}
                                onClick={() => handleLinkClick('Product List')}
                            >
                                Product List
                            </NavLink>
                        </li>
                    )}

                    {/* User's Product List */}
                    {isAuthenticated && (
                        <li>
                            <NavLink 
                                to="/product-list"
                                className={activeLink === 'View Products' ? 'active' : ''}
                                onClick={() => handleLinkClick('View Products')}
                            >
                                View Products
                            </NavLink>
                        </li>
                    )}


                    {/* Logout */}
                    {isAuthenticated && (
                        <li>
                            <NavLink to="/" onClick={handleLogout}>
                                Logout
                            </NavLink>
                        </li>
                    )}
                </ul>
            </nav>
        </>
    );
}

export default Navbar;
