import React from 'react';
import { NavLink, useNavigate, useLocation } from 'react-router-dom';
import UserService from '../service/UserService';

function Navbar() {
    const isAuthenticated = UserService.isAuthenticated();
    const isAdmin = UserService.isAdmin();
    const navigate = useNavigate();
    const location = useLocation();  // Get the current location

    const handleLogout = () => {
        const confirmDelete = window.confirm('Are you sure you want to logout this user?');
        if (confirmDelete) {
            UserService.logout();
            navigate('/');
        }
    };

    return (
        <>
            <style>
                {`
                    nav {
                        padding: 10px;
                        background-color: #333;
                    }
                    ul {
                        list-style: none;
                        padding: 0;
                    }
                    li {
                        display: inline-block;
                        margin: 0 15px;
                    }
                    a {
                        text-decoration: none;
                        color: #fff;
                    }
                    a:hover {
                        color: #f39c12;
                    }
                    .active-link {
                        color: #f39c12;
                        font-weight: bold;
                    }
                `}
            </style>
            <nav>
                <ul>
                    {/* E-Commerce Website link */}
                    {!isAuthenticated && (
                        <li>
                            <NavLink to="/" exact className={({ isActive }) => (isActive ? 'active-link' : '')}>
                                E-Commerce Website
                            </NavLink>
                        </li>
                    )}

                    {/* Profile link */}
                    {isAuthenticated && (
                        <li>
                            <NavLink
                                to="/profile"
                                className={location.pathname === '/profile' ? 'active-link' : ''}
                            >
                                Profile
                            </NavLink>
                        </li>
                    )}

                    {/* Admin Product Section */}
                    {isAdmin && isAuthenticated && (
                        <li>
                            <NavLink to="/auth/product-add" activeClassName="active-link">
                                Product Section
                            </NavLink>
                        </li>
                    )}

                    {/* Product List */}
                    {isAuthenticated && (
                        <li>
                            <NavLink to="/auth/all-products" activeClassName="active-link">
                                Product List
                            </NavLink>
                        </li>
                    )}

                    {/* User's Product List */}
                    {isAuthenticated && (
                        <li>
                            <NavLink to="/product-list" activeClassName="active-link">
                                Our Products
                            </NavLink>
                        </li>
                    )}

                    {/* Admin User Management */}
                    {isAdmin && (
                        <li>
                            <NavLink to="/admin/user-management" activeClassName="active-link">
                                User Management
                            </NavLink>
                        </li>
                    )}

                    {/* Logout */}
                    {isAuthenticated && (
                        <li>
                            <NavLink to="/" onClick={handleLogout} activeClassName="active-link">
                                Logout
                            </NavLink>
                        </li>
                    )}
                </ul>
            </nav>
        </>
    );
}

export default Navbar;
