import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import ProductService from '../service/ProductService';
import RatingReviewService from '../service/RatingReviewService';

function ViewProduct() {
  const { id } = useParams(); // Get the product ID from the URL
  const [product, setProduct] = useState(null);
  const [reviewDescription, setReviewDescription] = useState('');
  const [ratingValue, setRatingValue] = useState(0); // Store selected rating (1-5)

  useEffect(() => {
    const fetchProduct = async () => {
      try {
        const dummyProducts = await ProductService.getProductById(id);
        setProduct(dummyProducts.singleProduct);
      } catch (error) {
        console.log('Error fetching product:', error);
      }
    };

    fetchProduct();
  }, [id]);

  const handleReviewChange = (e) => {
    setReviewDescription(e.target.value);
  };

  const handleRatingChange = (e) => {
    setRatingValue(Number(e.target.value)); // Ensure rating is stored as a number
  };

  const handleSubmitReview = async (e) => {
    e.preventDefault();

    const RatRev = {
      rating_value: ratingValue,
      review_description: reviewDescription,
    };
    console.log(RatRev);
    try {
      await RatingReviewService.SaveRatingReview(RatRev);
      // Handle success (e.g., show a success message or clear inputs)
      console.log('Review saved successfully');
    } catch (error) {
      console.log('Error saving review:', error);
    }
  };

  if (!product) {
    return <div>Loading...</div>;
  }

  return (
    <div className="view-product-container">
      <div className="product-content">
        <div className="right-container">
          <h2 className="product-name">{product.product_name}</h2>
          <p className="product-price">${product.product_price}</p>
          <p className="product-description">{product.product_description}</p>

          {/* Review Section */}
          <div className="form-group">
            <label htmlFor="ratingValue">Rating (1-5):</label>
            <input
              type="number"
              id="ratingValue"
              name="ratingValue"
              min="1"
              max="5"
              value={ratingValue}
              onChange={handleRatingChange}
            />
          </div>
          <textarea
            className="review-textbox"
            placeholder="Write a review..."
            value={reviewDescription}
            onChange={handleReviewChange}
            name="reviewDescription"
          ></textarea>
          <button onClick={handleSubmitReview} className="submit-review-button">
            Submit Review
          </button>
        </div>
      </div>
    </div>
  );
}

export default ViewProduct;
