import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import ProductService from '../service/ProductService';

function ProductList() {
  const [products, setProducts] = useState([]);

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        // Fetch the products data
        const dummyProducts = await ProductService.getAllProducts();

        // For each product, fetch its reviews and calculate the average rating
        const productsWithReviews = await Promise.all(dummyProducts.products.map(async (product) => {
          const requestData = { product_id: product.product_id };
          const reviews = await ProductService.getProductAllRatingReviews(requestData); // Assuming this method exists

          // Log reviews and review count for debugging
          console.log("Reviews for product", product.product_id, reviews.ratingReviewDtoList);

          const reviewCount = reviews.ratingReviewDtoList.length;

          // Calculate the average rating using rating_value (assuming this is the field for the rating)
          const averageRating = reviewCount > 0 
            ? reviews.ratingReviewDtoList.reduce((sum, review) => sum + review.rating_value, 0) / reviewCount
            : 0;

          // Round the average rating to one decimal place for more consistent star filling
          const roundedAverageRating = Math.round(averageRating * 10) / 10;

          // Log average rating and review count for debugging
          console.log("Average rating:", roundedAverageRating, "Review count:", reviewCount);

          return {
            ...product,
            reviews,
            averageRating: roundedAverageRating,  // Use rounded average rating
            reviewCount,
          };
        }));

        // Set the products with the reviews and average ratings
        setProducts(productsWithReviews);
      } catch (error) {
        console.error("Error fetching products or reviews:", error);
      }
    };

    fetchProducts(); // Call the async function
  }, []);

  return (
    <div className="product-list-container">
      <h2 className="heading">Product List</h2>
      <div className="product-container">
        {products.map((product) => (
          <div className="product-card" key={product.product_id}>
            <img 
              src={`data:image/jpeg;base64,${product.product_image}`} 
              alt={product.product_name} 
              className="product-image" 
            />
            <h3 className="product-name">{product.product_name}</h3>
            <p className="product-description">{product.product_description}</p>
            <div className="product-rating">
              {/* Display filled and empty stars based on average rating */}
              {[...Array(5)].map((_, index) => (
                <span key={index} className={index < product.averageRating ? 'filled' : 'empty'}>
                  â˜…
                </span>
              ))}
              {/* Display the review count inside the parentheses */}
              {product.reviewCount !== undefined && (
                <span className="review-count">({product.reviewCount || 0})</span>
              )}
            </div>
            <Link to={`/view-product/${product.product_id}`} className="view-button">View</Link>
          </div>
        ))}
      </div>
    </div>
  );
}

export default ProductList;
