import React, { useState, useEffect } from 'react';
import UserService from '../service/UserService';
import { Link } from 'react-router-dom';
import './profile.css'; // Create a CSS file for styling
import './index.css';

function ProfilePage() {
    const [profileInfo, setProfileInfo] = useState({});

    useEffect(() => {
        fetchProfileInfo();
    }, []);

    const fetchProfileInfo = async () => {
        try {
            const token = localStorage.getItem('token'); // Retrieve the token from localStorage
            const response = await UserService.getYourProfile(token);
            setProfileInfo(response.ourUsers);
        } catch (error) {
            console.error('Error fetching profile information:', error);
        }
    };

    return (
        <div className="profile-page-container">
            <div className="profile-card">
                <h2 className="profile-title">Your Profile</h2>
                <div className="profile-details">
                    <div className="profile-item">
                        <strong>Name:</strong> <span>{profileInfo.name}</span>
                    </div>
                    <div className="profile-item">
                        <strong>Email:</strong> <span>{profileInfo.email}</span>
                    </div>
                    <div className="profile-item">
                        <strong>City:</strong> <span>{profileInfo.city}</span>
                    </div>
                </div>
                
                {profileInfo.role === "ADMIN" && (
                    <div className="admin-action">
                        <button className="update-button">
                            <Link to={`/update-user/${profileInfo.id}`} className="update-link">Update Profile</Link>
                        </button>
                    </div>
                )}
            </div>
        </div>
    );
}

export default ProfilePage;
