// components/UserManagementPage.js
import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import UserService from '../service/UserService';
import ProductService from '../service/ProductService';

function AllProducts() {
    
  const [users, setProducts] = useState([]);

  useEffect(() => {
    // Fetch users data when the component mounts
    fetchProducts();
  }, []);

  const fetchProducts = async () => {
    try {

      //const token = localStorage.getItem('token'); // Retrieve the token from localStorage
      const response = await ProductService.getAllProucts();
      //   console.log(response);
      setProducts(response.ourUsersList); // Assuming the list of users is under the key 'ourUsersList'
    } catch (error) {
      console.error('Error fetching users:', error);
    }
  };


  const deleteProduct = async (userId) => {
    try {
      // Prompt for confirmation before deleting the user
      const confirmDelete = window.confirm('Are you sure you want to delete this product?');

      if (confirmDelete) {
        await ProductService.deleteProductById(userId);
        // After deleting the user, fetch the updated list of users
        fetchProducts();
      }
    } catch (error) {
      console.error('Error deleting product:', error);
    }
  };

  return (
    <div className="product-management-container">
      <h2>Product Management Page</h2>
      {/* <button className='reg-button'> <Link to="/register">Add Product Details</Link></button> */}
      <table>
        <thead>
          <tr>
            {/* <th>ID</th> */}
            <th>Product Name</th>
            <th>Product Price</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {users.map(user => (
            <tr key={user.id}>
              <td>{user.id}</td>
              <td>{user.name}</td>
              <td>{user.email}</td>
              <td>
                <button className='delete-button' onClick={() => deleteUser(user.id)}>Delete</button>
                <button><Link to={`/update-user/${user.id}`}>
                  Update
                </Link>
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default AllProducts;
