import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import ProductService from '../service/ProductService';

const getImageMimeType = (imageData) => {
  if (imageData.startsWith('data:image/png;base64,')) {
    return 'image/png';
  } else if (imageData.startsWith('data:image/jpeg;base64,')) {
    return 'image/jpeg';
  }
  return ''; // Handle other types or defaults
};

function ProductList() {
  const [products, setProducts] = useState([]);

  useEffect(() => {
    const fetchProducts = async () => {
      const dummyProducts = await ProductService.getAllProducts();
      setProducts(dummyProducts.products);
    };

    fetchProducts();
  }, []);

  return (
    <div className="product-list-container">
      <h2 className="heading">Product List</h2>
      <div className="product-container">
        {products.map((product) => {
          // Get MIME type for image dynamically
          const mimeType = getImageMimeType(product.product_image);

          // Prepend the MIME type (if not already done) and render the image
          const imageSrc = mimeType ? `data:${mimeType};base64,${product.product_image}` : null;

          return (
            <div className="product-card" key={product.product_id}>
              {product.product_image ? (
                <img
                  src={imageSrc}
                  alt={product.product_name}
                  className="product-image"
                />
              ) : null}
              <h3 className="product-name">{product.product_name}</h3>
              <p className="product-description">{product.product_description}</p>
              <div className="product-rating">
                {[...Array(5)].map((_, index) => (
                  <span key={index} className={index < product.rating ? 'filled' : 'empty'}>
                    â˜…
                  </span>
                ))}
              </div>
              <Link to={`/view-product/${product.product_id}`} className="view-button">
                View
              </Link>
            </div>
          );
        })}
      </div>
    </div>
  );
}
