import React, { useState, useEffect } from 'react';
import { useNavigate, Link, useParams } from 'react-router-dom';
import RatingReviewService from '../service/RatingReviewService';
import ProductService from '../service/ProductService';

function ViewProduct() {
  const { prodId } = useParams();
  const navigate = useNavigate();
  const userId = localStorage.getItem('userId');

  const [prodData, setProdData] = useState({
    product_name: '',
    product_price: '',
    product_description: '',
    product_image: null
  });

  const [reviews, setReviews] = useState([]);
  const [isEditing, setIsEditing] = useState(false);
  const [editingReviewId, setEditingReviewId] = useState(null);

  const [formData, setFormData] = useState({
    rating_value: 0,
    review_description: '',
    product_id: prodId,
    user_id: userId
  });

  useEffect(() => {
    fetchProductDataById(prodId);
    checkIfReviewExists(prodId, userId);
  }, [prodId]);

  const fetchProductDataById = async (prodId) => {
    try {
      const response = await ProductService.getProductById(prodId);
      const { product_name, product_price, product_description, product_image } = response.singleProduct;
      setProdData({ product_name, product_price, product_description, product_image });
    } catch (error) {
      console.error('Error fetching product data:', error);
    }
  };

  const checkIfReviewExists = async (prodId, userId) => {
    try {
      const requestData = { product_id: prodId, user_id: userId };
      const reviewResponse = await RatingReviewService.getExistRatingReviewByUserId(requestData);
      setReviews(reviewResponse.ratingReviewDtoList);
    } catch (error) {
      console.error('Error checking review:', error);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleStarClick = (index) => {
    setFormData({ ...formData, rating_value: index + 1 });
  };

  const handleSubmitReview = async (e) => {
    e.preventDefault();
    try {
      if (isEditing) {
        // Update existing review
        await RatingReviewService.updateRatingReview(editingReviewId, formData);
        alert('Review updated successfully');
        setIsEditing(false);
        setEditingReviewId(null);
      } else {
        // Create new review
        await RatingReviewService.SaveRatingReview(formData);
        alert('Review submitted successfully');
      }
      setFormData({ rating_value: 0, review_description: '' });
      checkIfReviewExists(prodId, userId); // Refresh reviews
    } catch (error) {
      console.error('Error submitting review:', error);
      alert('An error occurred while submitting the review');
    }
  };

  const handleEditClick = (review) => {
    setIsEditing(true);
    setEditingReviewId(review.rating_review_id);
    setFormData({
      rating_value: review.rating_value,
      review_description: review.review_description,
      product_id: review.product_id,
      user_id: review.user_id
    });
  };

  return (
    <div className="view-product-container" style={{ display: 'flex', flexDirection: 'column', alignItems: 'center', padding: '20px' }}>
      {/* Product Section */}
      <div className="product-content" style={{ display: 'flex', flexWrap: 'wrap', gap: '20px', marginBottom: '30px', maxWidth: '1200px' }}>
        <div className="left-container" style={{ flex: '1', textAlign: 'center' }}>
          <img
            src={`data:image/jpeg;base64,${prodData.product_image}`}
            alt={prodData.product_name}
            className="product-image"
            style={{ maxWidth: '100%', borderRadius: '8px' }}
          />
        </div>
        <div className="right-container" style={{ flex: '1' }}>
          <h2 className="product-name" style={{ fontSize: '24px', fontWeight: 'bold' }}>{prodData.product_name}</h2>
          <p className="product-price" style={{ fontSize: '20px', color: 'green', marginBottom: '10px' }}>${prodData.product_price}</p>
          <p className="product-description" style={{ marginBottom: '20px' }}>{prodData.product_description}</p>

          {/* Add/Edit Review Section */}
          <div className="add-review" style={{ marginTop: '20px' }}>
            <h3>{isEditing ? 'Edit Your Review' : 'Write Your Review'}</h3>
            <div className="star-rating" style={{ fontSize: '24px' }}>
              {[...Array(5)].map((_, index) => (
                <span
                  key={index}
                  className={index < formData.rating_value ? 'filled' : 'empty'}
                  onClick={() => handleStarClick(index)}
                  style={{ cursor: 'pointer', color: index < formData.rating_value ? 'gold' : 'lightgray' }}
                >
                  ★
                </span>
              ))}
            </div>
            <textarea
              className="review-textbox"
              name="review_description"
              placeholder="Write your review here..."
              value={formData.review_description}
              onChange={handleInputChange}
              required
              style={{ width: '100%', padding: '10px', marginTop: '10px', borderRadius: '5px', borderColor: '#ddd', fontSize: '16px' }}
            ></textarea>
            <button
              onClick={handleSubmitReview}
              className="submit-review-button"
              style={{
                backgroundColor: 'blue',
                color: 'white',
                padding: '10px 15px',
                border: 'none',
                borderRadius: '4px',
                cursor: 'pointer',
                marginTop: '10px'
              }}
            >
              {isEditing ? 'Update Review' : 'Submit Review'}
            </button>
          </div>
        </div>
      </div>

      {/* Existing Reviews Section */}
      <div className="existing-reviews" style={{ width: '100%', maxWidth: '800px', marginTop: '30px' }}>
        <h3>Customer Reviews</h3>
        {reviews.length > 0 ? (
          <div className="reviews-container" style={{ display: 'flex', flexDirection: 'column', gap: '15px' }}>
            {reviews.map((review) => (
              <div key={review.rating_review_id} className="review-card" style={{ border: '1px solid #ddd', padding: '15px', borderRadius: '8px', backgroundColor: '#f9f9f9' }}>
                <div className="review-header" style={{ display: 'flex', justifyContent: 'space-between' }}>
                  <div className="review-rating">
                    {[...Array(review.rating_value)].map((_, index) => (
                      <span key={index} className="filled" style={{ fontSize: '20px', color: 'gold' }}>★</span>
                    ))}
                  </div>
                  {review.user_id === userId && (
                    <button
                      className="edit-button"
                      onClick={() => handleEditClick(review)}
                      style={{
                        backgroundColor: 'blue',
                        color: 'white',
                        border: 'none',
                        padding: '5px 10px',
                        borderRadius: '4px',
                        cursor: 'pointer'
                      }}
                    >
                      Edit
                    </button>
                  )}
                </div>
                <p className="review-description" style={{ marginTop: '10px' }}>{review.review_description}</p>
              </div>
            ))}
          </div>
        ) : (
          <p>No reviews yet for this product.</p>
        )}
      </div>

      <Link to="/product-list" className="back-link" style={{ marginTop: '20px', color: 'blue', fontWeight: 'bold', textDecoration: 'underline' }}>
        Back to Product List
      </Link>
    </div>
  );
}

export default ViewProduct;
