import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import ProductService from '../service/ProductService';

function ProductList() {
  const [products, setProducts] = useState([]);

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        // Fetch the products data
        const dummyProducts = await ProductService.getAllProducts();

        const productsWithReviews = await Promise.all(dummyProducts.products.map(async (product) => {
          const requestData = { product_id: product.product_id };
          let reviews = { ratingReviewDtoList: [] }; // Default to an empty array

          try {
            reviews = await ProductService.getProductAllRatingReviews(requestData);
            console.log(`API Response for product ${product.product_id}:`, reviews);
          } catch (err) {
            console.error(`Error fetching reviews for product ${product.product_id}:`, err);
          }

          const reviewList = reviews.ratingReviewDtoList || []; // Safely access ratingReviewDtoList
          const reviewCount = reviewList.length;

          const averageRating = reviewCount > 0
            ? reviewList.reduce((sum, review) => sum + (review.rating_value || 0), 0) / reviewCount
            : 0;

          const roundedAverageRating = Math.round(averageRating * 10) / 10;

          return {
            ...product,
            reviews,
            averageRating: roundedAverageRating,
            reviewCount,
          };
        }));

        setProducts(productsWithReviews);
      } catch (error) {
        console.error("Error fetching products or reviews:", error);
      }
    };

    fetchProducts(); // Call the async function
  }, []);

  return (
    <div className="product-list-container">
      <h2 className="heading">Product List</h2>
      <div className="product-container">
        {products.map((product) => (
          <div className="product-card" key={product.product_id}>
            <img
              src={`data:image/jpeg;base64,${product.product_image}`}
              alt={product.product_name}
              className="product-image"
            />
            <h3 className="product-name">{product.product_name}</h3>
            <p className="product-description">{product.product_description}</p>
            <div className="product-rating">
              {[...Array(5)].map((_, index) => (
                <span key={index} className={index < product.averageRating ? 'filled' : 'empty'}>
                  ★
                </span>
              ))}
              {product.reviewCount !== undefined && (
                <span className="review-count" style={{ color: '#878787', fontSize: '15px' }}>
                  ({product.reviewCount || 0})
                </span>
              )}
            </div>
            <Link to={`/view-product/${product.product_id}`} className="view-button">View</Link>
          </div>
        ))}
      </div>

      <style>
        {`
          .product-list-container {
            margin: 20px auto;
            padding: 20px;
            max-width: 1200px; /* Fixed maximum width for the entire container */
          }

          .heading {
            text-align: center;
            font-size: 2rem;
            margin-bottom: 20px;
            color: #333;
          }

          .product-container {
            display: grid;
            grid-template-columns: repeat(5, 1fr); /* 5 items in each row */
            gap: 20px; /* Space between grid items */
            justify-content: center;
            align-content: center;
            max-width: 1200px; /* Container fixed width */
            margin: 0 auto;
          }

          .product-card {
            width: 200px; /* Fixed width for each card */
            background-color: #fff;
            border-radius: 10px;
            box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);
            overflow: hidden;
            padding: 15px;
            text-align: center;
            transition: transform 0.3s ease, box-shadow 0.3s ease;
          }

          .product-card:hover {
            transform: translateY(-10px);
            box-shadow: 0 6px 12px rgba(0, 0, 0, 0.2);
          }

          .product-image {
            width: 180px;  /* Fixed width for images */
            height: 180px; /* Fixed height for images */
            object-fit: cover; /* Ensures image aspect ratio is maintained */
            border-radius: 10px;
            margin: 0 auto;
          }

          .product-name {
            font-size: 1rem;
            margin-top: 10px;
            font-weight: bold;
            color: #333;
          }

          .product-description {
            font-size: 0.9rem;
            color: #555;
            margin: 10px 0;
          }

          .product-rating {
            display: flex;
            align-items: center;
            justify-content: center;
            margin-top: 10px;
            margin-botton:10px;
          }

          .filled {
            color: #ff9800;
          }

          .empty {
            color: #dcdcdc;
          }

          .review-count {
            margin-left: 10px;
          }

          .view-button {
            margin-top: 150px;
            padding: 10px 20px;
            background-color: #007bff;
            color: white;
            border: none;
            border-radius: 5px;
            text-decoration: none;
            font-weight: bold;
            font-size: 0.9rem;
          }

          .view-button:hover {
            background-color: #0056b3;
          }
        `}
      </style>
    </div>
  );
}

export default ProductList;
