import React, { useState, useEffect } from 'react';
import { useParams, useNavigate,Link } from 'react-router-dom';
import UserService from '../service/UserService';
import ProductService from '../service/ProductService';

function UpdateProduct() {
    
  const navigate = useNavigate();
  const { prodId } = useParams();

  console.log(prodId);

  const [prodData, setProdData] = useState({
        product_name: '',
        product_price: '',
        product_description: ''
  });

  useEffect(() => {
    fetchProductDataById(prodId); // Pass the userId to fetchUserDataById
  }, [prodId]); //wheen ever there is a chane in userId, run this

  const fetchProductDataById = async (prodId) => {
    try {
    
      const response = await ProductService.getProductById(prodId); // Pass userId to getUserById
      console.log(response);
      const { product_name, product_price, product_description } = response.singleProduct      ;

      setProdData({ product_name, product_price, product_description })
     
    } catch (error) {
      console.error('Error fetching user data:', error);
    }
  };


  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setProdData((prevProdData) => ({
      ...prevProdData,
      [name]: value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const confirmDelete = window.confirm('Are you sure you want to delete this user?');
      if (confirmDelete) {
        //const token = localStorage.getItem('token');
        const res = await ProductService.updateProduct(prodId, prodData);
        console.log(res)
        // Redirect to profile page or display a success message
       // navigate("/admin/user-management")
      }

    } catch (error) {
      console.error('Error updating user profile:', error);
      alert(error)
    }
  };

  return (
    <div className="auth-container">
      <h2>Update Product</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label>Name:</label>
          <input type="text" name="name" value={prodData.product_name} onChange={handleInputChange} />
        </div>
        <div className="form-group">
          <label>price:</label>
          <input type="email" name="email" value={prodData.product_price} onChange={handleInputChange} />
        </div>
        <div className="form-group">
          <label>description:</label>
          <input type="text" name="role" value={prodData.product_description} onChange={handleInputChange} />
        </div>
        <button type="submit">Update</button>
        <br/>
        <Link to="/profile" style={{ color: 'blue', fontWeight: 'bold', textDecoration: 'underline' }}>Back to profile</Link>
      </form>
    </div>
  );
}

export default UpdateProduct;
